const negoConf = require(process.cwd(),'negodb.config.js')
module.exports= class nego{
   analize(file){
   if(!file) return new TypeError("31")
     var analizeJson = fs.statSync(file).size
  if(analizeJson > negoConf.max_usage){
    return new TypeError("NEGODB | Maximum Usage Limit Has Exceeded | If You Want To Increase Limit Edit **negodb.config.js** File ! ")
  }
  
}
}
